mapper.insert:
INSERT INTO TBL_DOG (
DOG_CODE
, MEMBER_CODE
, CLASS_CODE
,
DOG_NAME
, GENDER
, BIRTH
, DOG_BREED
, WEIGHT
, CHIP_NO
, NOTES
, JOIN_DATE
,
WITHDRAW_DATE
, STATUS
)
VALUES (
#{dogCode}
, #{memberCode}
, #{classCode}
,
#{dogName}
, #{gender}
, #{birth}
, #{dogBreed}
, #{weight}
, #{chipNo}
,
#{notes}
, current_date
, #{withdrawDate}
, #{status}
)

mapper.update:
UPDATE dogs
SET member_code = #{memberCode}
, class_code =
#{classCode}
, dog_name = #{dogName}
, gender = #{gender}
, birth =
#{birth}
, dog_breed = #{dogBreed}
, weight = #{weight}
, chip_no =
#{chipNo}
, notes = #{notes}
, join_date = #{joinDate}
, withdraw_date =
#{withdrawDate}
, status = #{status}
WHERE dog_code = #{dogCode}

UPDATE dogs
SET status = 'N'
WHERE dog_code = #{dogCode}
