mapper.insert:
INSERT INTO 
		TBL_MEMBER (
					MEMBER_ID
					, MEMBER_PWD
					, MEMBER_BIRTH
					, MEMBER_NAME
					, PHONE
					, ADDRESS
					, ROLE_CODE
					, CLASS_CODE
					)
		VALUES (
				#{ memberId }
				, #{ memberPwd }
				, STR_TO_DATE(#{memberBirth}, '%Y%m%d')
				, #{ memberName }
				, #{ phone }
				, #{ address }
				, #{ roleCode }
				, #{ classCode }
				)

mapper.select:
SELECT MEMBER_PWD
FROM TBL_MEMBER
WHERE MEMBER_ID = #{ memberId }

SELECT MEMBER_CODE, MEMBER_ID, MEMBER_NAME, ROLE_CODE, MEMBER_BIRTH, ADDRESS, PHONE, STATUS
FROM TBL_MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE TBL_MEMBER
SET PHONE = #{ changePhone }, ADDRESS = #{ changeAddress }
WHERE MEMBER_CODE = #{ requestMemberCode }

UPDATE TBL_MEMBER
SET PHONE = #{ changePhone }, ADDRESS = #{ changeAddress }, CLASS_CODE = #{ changeClassCode }
WHERE MEMBER_CODE = #{ requestMemberCode }

UPDATE TBL_MEMBER
SET STATUS = #{ deleteMemberSatus }, WITHDRAW_DATE = NOW()
WHERE MEMBER_CODE = #{ deleteMemberCode }

mapper.select:
	SELECT *
	FROM TBL_MEMBER
	WHERE ROLE_CODE = 2

            AND CLASS_CODE = #{classFilter}


            AND JOIN_DATE BETWEEN #{startDate} AND #{endDate}

	ORDER BY 

		STATUS DESC, JOIN_DATE
		CLASS_CODE, JOIN_DATE

			JOIN_DATE


	LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM TBL_MEMBER
WHERE ROLE_CODE = 2

    AND CLASS_CODE = #{classFilter}


    AND JOIN_DATE BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(*) > 0
FROM TBL_MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE TBL_MEMBER
SET STATUS = TRUE
WHERE MEMBER_ID = #{ memberId }
