mapper.insert:
INSERT INTO 
      TBL_STICKER(
               DOG_CODE
               , MEMBER_CODE
               , STIKER_NUM
               , COMMENT
               , REGIST_DATE
               )
      VALUES (
            #{ dogCode }
            , #{ memerCode }
            , #{ stickerNum }
            , #{ comment }
            , #{ registDate }               

            )

mapper.update:
	 UPDATE  TBL_STICKER
	 SET
                DOG_CODE = #{ dogCode }                  
                , MEMBER_CODE = #{ memerCode }
                , STIKER_NUM = #{ stickerNum }
                , COMMENT = #{ comment }
                , REGIST_DATE = #{ registDate }   
   WHERE STICKER_CODE = #{ stickerCode }
