mapper.insert:
INSERT INTO 
      TBL_STICKER(
               DOG_CODE
               , MEMBER_CODE
               , STICKER_NUM
               , COMMENT
               , REGIST_DATE
               )
      VALUES (
            #{ dogCode }
            , #{ memberCode }
            , #{ stickerNum }
            , #{ comment }
            , #{ registDate }               

            )

mapper.update:
	 UPDATE  TBL_STICKER
	 SET
                DOG_CODE = #{ dogCode }                  
                , MEMBER_CODE = #{ memberCode }
                , STICKER_NUM = #{ stickerNum }
                , COMMENT = #{ comment }
                , REGIST_DATE = #{ registDate }   
   WHERE STICKER_CODE = #{ stickerCode }

mapper.select:
SELECT DOG_CODE, DOG_NAME
FROM TBL_DOG
ORDER BY DOG_NAME ASC;
